jQuery(window).load(function(){jQuery(".item img").fadeIn(500);jQuery(".item img").each(function(){var a=jQuery(this);a.css({position:"absolute"}).wrap("<div class='img_wrapper' style='display: inline-block'>").clone().addClass("img_grayscale").css({position:"absolute","z-index":"998",opacity:"0"}).insertBefore(a).queue(function(){var b=jQuery(this);b.parent().height(65);b.dequeue()});this.src=grayscale(this.src)});jQuery(".item img").mouseover(function(){jQuery(this).parent().find("img:first").stop().animate({opacity:1},1000)});jQuery(".img_grayscale").mouseout(function(){jQuery(this).stop().animate({opacity:0},1000)})});function grayscale(a){var c=document.createElement("canvas");var j=c.getContext("2d");var d=new Image();d.src=a;c.width=d.width;c.height=d.height;j.drawImage(d,0,0);var b=j.getImageData(0,0,c.width,c.height);for(var g=0;g<b.height;g++){for(var h=0;h<b.width;h++){var e=(g*4)*b.width+h*4;var f=(b.data[e]+b.data[e+1]+b.data[e+2])/3;b.data[e]=f;b.data[e+1]=f;b.data[e+2]=f}}j.putImageData(b,0,0,0,0,b.width,b.height);return c.toDataURL()};